package com.jaylabs.recyclerviewadapterevening

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.jaylabs.recyclerviewadapterevening.Model.ImageFromNet
import com.jaylabs.recyclerviewadapterevening.adapter.ImageFromNetAdapter

class MainActivity : AppCompatActivity() {

    private lateinit var recyclerView:RecyclerView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        recyclerView = findViewById(R.id.recyclerView)
        // this creates a vertical layout Manager
        recyclerView.layoutManager = LinearLayoutManager(this)

        //ArrayList of class ItemsViewModel
        val imgFromNet = ArrayList<ImageFromNet>()
        imgFromNet.add(ImageFromNet("https://media.istockphoto.com/photos/white-king-chess-standing-against-black-background-picture-id1154467417?k=20&m=1154467417&s=612x612&w=0&h=8ILQ3HHMtcLrzhSjWOD3LMjL90wXWYLXHI3_Wn49keo=","King","The most Valuable piece"))
        imgFromNet.add(ImageFromNet("https://media.istockphoto.com/photos/red-queen-chess-standing-against-black-background-picture-id1154467846?k=20&m=1154467846&s=612x612&w=0&h=Sop2WwVftY-U_9T8wfn8G65ttxI4v4_tnZoeNdChNfQ=","Queen","The most Powerful piece"))
        imgFromNet.add(ImageFromNet("data:image/jpeg;base64,","Rook","The Machine Gun"))
        imgFromNet.add(ImageFromNet("https://ak.picdn.net/shutterstock/videos/12577823/thumb/12.jpg","Bishop","The Diagonal Attack"))


        val adapta = ImageFromNetAdapter(this,imgFromNet)

        recyclerView.adapter = adapta




    }
}